con:config.script:
import java.text.SimpleDateFormat

def today = new Date()
def date = today + 30

// 2023-01-15T15:28:44.481Z
def dateFormat = new SimpleDateFormat("yyyy-MM-dd")
def timeFormat = new SimpleDateFormat("hh:mm:ss")
return dateFormat.format(date)+ "T" + timeFormat.format(date) + ".481"

con:configuration.scriptText:
def slurper = new groovy.json.JsonSlurper()
def result = slurper.parseText(messageExchange.responseContent)

for(int i = 0; i < result.General.size(); i++){
	log.info "item: " + result.item[i].id + " : " + result.item[i].desiredStock + " > " + result.currentQuantity[i]
	assert result.item[i].desiredStock > result.currentQuantity[i] || result.currentQuantity[i] == 0
}

con:config.script:
import java.text.SimpleDateFormat

def date = new Date()

// 2023-01-15T15:28:44.481Z
def dateFormat = new SimpleDateFormat("yyyy-MM-dd")
def timeFormat = new SimpleDateFormat("hh:mm:ss.S")
return dateFormat.format(date)+ "T" + timeFormat.format(date)

def orderId = testRunner.testCase.testSuite.getPropertyValue( "orderId" )
def strippedOrderId= orderId.replaceAll("\\[", "").replaceAll("\\]","");
log.info strippedOrderId
testRunner.testCase.testSuite.setPropertyValue("orderID", strippedOrderId)

def quantity = testRunner.testCase.testSuite.getPropertyValue( "quantity" )
def strippedQuantity = quantity.replaceAll("\\[", "").replaceAll("\\]","");
log.info strippedQuantity
testRunner.testCase.testSuite.setPropertyValue("quantity", strippedQuantity)

//Import packages that contain libraries needed to parse JSON and XML
import groovy.json.*
import groovy.util.*

//Parse JSON content in the request
def response = context.expand( '${Get Delivery#Response}' )
def parsedJson = new JsonSlurper().parseText(response)

def count = parsedJson.orders.size() - 1
log.info count

testRunner.testCase.testSuite.setPropertyValue("orderCount", count.toString())

